mapper.select:
select *, id as idStr
from t_bet_record


        and id = #{id}


        and user_id = #{userId}


        and bet_amount = #{betAmount}


        and can_win_amount = #{canWinAmount}


        and bet_content = #{betContent}


        and display_bet_content = #{displayBetContent}


        and bet_odds = #{betOdds}


        and bet_additional_odds = #{betAdditionalOdds}


        and platform_id = #{platformId}


        and platform_user_id = #{platformUserId}


        and lottery_id = #{lotteryId}


        and issue_no = #{issueNo}


        and play_type_name like concat('%', #{playTypeName}, '%')


        and win = #{win}


        and win_lose_amount = #{winLoseAmount}


        and play_type_code = #{playTypeCode}


        and play_type_detail_code = #{playTypeDetailCode}


        and bet_no = #{betNo}


        and status = #{status}


        and cancel_type = #{cancelType}


        and bet_time = #{betTime}


        and count_time = #{countTime}


        and create_by = #{createBy}


        and update_by = #{updateBy}


        and reward_status = #{rewardStatus}


        and platform_user_id in

            #{platformUserId}



        and status in

            #{status}





                and bet_time >= #{beginTime}


                and count_time >= #{beginTime}


                and bet_time >= #{beginTime}






                and bet_time <= #{endTime}


                and count_time <= #{endTime}


                and bet_time <= #{endTime}




order by bet_time desc

mapper.delete:
delete
from t_bet_record where id in

    #{id}

mapper.select:
select count(*)                as totalCount
     , count(distinct user_id) as totalBetUserCount
     , sum(bet_amount)         as totalAmount
     , sum(valid_bet_amount)   as totalValidBetAmount
     , sum(win_lose_amount)    as winLoseAmount
     , sum(win_amount)         as totalWinAmount
from t_bet_record


        and platform_id = #{platformId}


        and lottery_id = #{lotteryId}


        and user_id = #{userId}


        and issue_no = #{issueNo}


        and status = #{status}


        and play_type_code = #{playTypeCode}


        and play_type_detail_code = #{playTypeDetailCode}


        and create_time >= #{beginTime}


        and create_time <= #{endTime}

select user_id               as userId
     , count(*)              as totalCount
     , sum(bet_amount)       as totalAmount
     , sum(valid_bet_amount) as totalValidBetAmount
     , sum(win_lose_amount)  as winLoseAmount
     , sum(win_amount)       as totalWinAmount
from t_bet_record


        and platform_id = #{platformId}


        and lottery_id = #{lotteryId}


        and user_id = #{userId}


        and issue_no = #{issueNo}


        and status = #{status}


        and play_type_code = #{playTypeCode}


        and play_type_detail_code = #{playTypeDetailCode}


        and create_time >= #{beginTime}


        and create_time < #{endTime}


group by user_id

select date (create_time) as betDate, user_id as userId, count (*) as totalCount, sum (bet_amount) as totalAmount, sum (valid_bet_amount) as totalValidBetAmount
        , sum (win_lose_amount) as winLoseAmount, sum (win_amount) as totalWinAmount
from t_bet_record


        and platform_id = #{platformId}


        and lottery_id = #{lotteryId}


        and user_id = #{userId}


        and issue_no = #{issueNo}


        and status = #{status}


        and play_type_code = #{playTypeCode}


        and play_type_detail_code = #{playTypeDetailCode}


        and create_time >=#{beginTime}


        and create_time
       <#{endTime}


group by date (create_time)
order by betDate desc

select lottery_id            as lotteryId
     , count(*)              as totalCount
     , sum(bet_amount)       as totalAmount
     , sum(valid_bet_amount) as totalValidBetAmount
     , sum(win_lose_amount)  as winLoseAmount
     , sum(win_amount)       as totalWinAmount
from t_bet_record


        and platform_id = #{platformId}


        and lottery_id = #{lotteryId}


        and user_id = #{userId}


        and issue_no = #{issueNo}


        and status = #{status}


        and play_type_code = #{playTypeCode}


        and play_type_detail_code = #{playTypeDetailCode}


        and create_time >= #{beginTime}


        and create_time < #{endTime}


group by lottery_id

select play_type_code        as playTypeCode
     , count(*)              as totalCount
     , sum(bet_amount)       as totalAmount
     , sum(valid_bet_amount) as totalValidBetAmount
     , sum(win_lose_amount)  as winLoseAmount
     , sum(win_amount)       as totalWinAmount
from t_bet_record


        and platform_id = #{platformId}


        and lottery_id = #{lotteryId}


        and user_id = #{userId}


        and issue_no = #{issueNo}


        and status = #{status}


        and play_type_code = #{playTypeCode}


        and play_type_detail_code = #{playTypeDetailCode}


        and create_time >= #{beginTime}


        and create_time < #{endTime}


group by play_type_code

select play_type_detail_code as playTypeDetailCode
     , count(*)              as totalCount
     , sum(bet_amount)       as totalAmount
     , sum(valid_bet_amount) as totalValidBetAmount
     , sum(win_lose_amount)  as winLoseAmount
     , sum(win_amount)       as totalWinAmount
from t_bet_record


        and platform_id = #{platformId}


        and lottery_id = #{lotteryId}


        and user_id = #{userId}


        and issue_no = #{issueNo}


        and status = #{status}


        and play_type_code = #{playTypeCode}


        and play_type_detail_code = #{playTypeDetailCode}


        and create_time >= #{beginTime}


        and create_time < #{endTime}


group by play_type_detail_code

SELECT MAX(br.bet_content) AS number, br.play_type_detail_code AS playTypeDetailCode, COUNT(DISTINCT br.user_id) AS userCount, COUNT(*) AS orderCount, SUM(br.bet_amount) AS totalBetAmount, SUM(br.can_win_amount) AS totalCanWinAmount, lr.color AS colour, lr.sx AS sx, -- 大小判断 CONCAT( CASE WHEN CAST(MAX(br.bet_content) AS UNSIGNED) = 49 THEN '和' WHEN CAST(MAX(br.bet_content) AS UNSIGNED) >= 25 THEN '大' ELSE '小' END, '/', -- 单双判断 CASE WHEN CAST(MAX(br.bet_content) AS UNSIGNED) = 49 THEN '和' WHEN CAST(MAX(br.bet_content) AS UNSIGNED) % 2 = 0 THEN '双' ELSE '单' END ) AS type, RIGHT(MAX(br.bet_content),1) AS mantissa FROM t_bet_record br LEFT JOIN t_lottery_relation lr ON lr.number = br.bet_content WHERE br.lottery_id = #{lotteryId} AND lr.year = 2025 AND br.play_type_code = #{playTypeCode} AND br.play_type_detail_code BETWEEN 1 AND 49
AND br.user_id = #{userId}
AND br.issue_no = #{issueNo}
GROUP BY br.play_type_detail_code ORDER BY totalBetAmount DESC

SELECT base.sx, IFNULL(sx_numbers.numbers, '') AS number, COALESCE(t.userCount, 0) AS userCount, COALESCE(t.orderCount, 0) AS orderCount, COALESCE(t.totalBetAmount, 0) AS totalBetAmount, COALESCE(t.totalCanWinAmount, 0) AS totalCanWinAmount FROM (SELECT '鼠' AS sx UNION ALL SELECT '牛' UNION ALL SELECT '虎' UNION ALL SELECT '兔' UNION ALL SELECT '龙' UNION ALL SELECT '蛇' UNION ALL SELECT '马' UNION ALL SELECT '羊' UNION ALL SELECT '猴' UNION ALL SELECT '鸡' UNION ALL SELECT '狗' UNION ALL SELECT '猪') base LEFT JOIN ( SELECT br.bet_content AS sx, COUNT(DISTINCT br.user_id) AS userCount, COUNT(*) AS orderCount, SUM(br.bet_amount) AS totalBetAmount, SUM(br.can_win_amount) AS totalCanWinAmount FROM t_bet_record br WHERE br.lottery_id = #{lotteryId} AND br.play_type_code = #{playTypeCode} AND br.play_type_detail_code BETWEEN 1130 AND 1141
AND br.user_id = #{userId}
AND br.issue_no = #{issueNo}
GROUP BY br.bet_content ) t ON base.sx = t.sx LEFT JOIN (SELECT sx, GROUP_CONCAT(number ORDER BY number ASC) AS numbers FROM t_lottery_relation WHERE `year` = 2025 GROUP BY sx) sx_numbers ON base.sx = sx_numbers.sx GROUP BY base.sx ORDER BY totalBetAmount DESC, FIELD(base.sx, '鼠', '牛', '虎', '兔', '龙', '蛇', '马', '羊', '猴', '鸡', '狗', '猪');
