mapper.select:
select * from t_bet_record

     and id = #{id}
     and user_id = #{userId}
     and bet_amount = #{betAmount}
     and can_win_amount = #{canWinAmount}
     and bet_content = #{betContent}
     and display_bet_content = #{displayBetContent}
     and bet_odds = #{betOdds}
     and bet_additional_odds = #{betAdditionalOdds}
     and platform_id = #{platformId}
     and platform_user_id = #{platformUserId}
     and lottery_id = #{lotteryId}
     and issue_no = #{issueNo}
     and play_type_name like concat('%', #{playTypeName}, '%')
     and win = #{win}
     and win_lose_amount = #{winLoseAmount}
     and play_type_code = #{playTypeCode}
     and play_type_detail_code = #{playTypeDetailCode}
     and bet_no = #{betNo}
     and status = #{status}
     and cancel_type = #{cancelType}
     and bet_time = #{betTime}
     and count_time = #{countTime}
     and create_by = #{createBy}
     and update_by = #{updateBy}
     and reward_status = #{rewardStatus}

      and platform_user_id in

          #{platformUserId}



        and status in

            #{status}





                and bet_time >= #{beginTime}


                and count_time >= #{beginTime}


                and bet_time >= #{beginTime}






                and bet_time <= #{endTime}


                and count_time <= #{endTime}


                and bet_time <= #{endTime}




    order by bet_time desc

mapper.update:
update t_bet_record
user_id = #{userId}, bet_amount = #{betAmount}, can_win_amount = #{canWinAmount}, bet_content = #{betContent}, display_bet_content = #{displayBetContent}, bet_odds = #{betOdds}, bet_additional_odds = #{betAdditionalOdds}, platform_id = #{platformId}, platform_user_id = #{platformUserId}, lottery_id = #{lotteryId}, issue_no = #{issueNo}, play_type_name = #{playTypeName}, win = #{win}, win_lose_amount = #{winLoseAmount}, play_type_code = #{playTypeCode}, play_type_detail_code = #{playTypeDetailCode}, bet_no = #{betNo}, status = #{status}, cancel_type = #{cancelType}, bet_time = #{betTime}, count_time = #{countTime}, create_time = #{createTime}, update_time = #{updateTime}, create_by = #{createBy}, update_by = #{updateBy},
where id = #{id}

mapper.select:
select count(*) as totalCount,count(distinct user_id) as totalBetUserCount,sum(bet_amount) as totalAmount,sum(valid_bet_amount) as totalValidBetAmount
,sum(win_lose_amount) as winLoseAmount,sum(win_amount) as totalWinAmount from t_bet_record


        and platform_id = #{platformId}


        and lottery_id = #{lotteryId}


        and user_id = #{userId}


        and issue_no = #{issueNo}


        and status = #{status}


        and play_type_code = #{playTypeCode}


        and play_type_detail_code = #{playTypeDetailCode}


        and create_time >=#{beginTime}


        and create_time <=#{endTime}

select user_id as userId, count(*) as totalCount,sum(bet_amount) as totalAmount,sum(valid_bet_amount) as totalValidBetAmount
,sum(win_lose_amount) as winLoseAmount,sum(win_amount) as totalWinAmount from t_bet_record


        and platform_id = #{platformId}


        and lottery_id = #{lotteryId}


        and user_id = #{userId}


        and issue_no = #{issueNo}


        and status = #{status}


        and play_type_code = #{playTypeCode}


        and play_type_detail_code = #{playTypeDetailCode}


        and create_time >=#{beginTime}


        and create_time <#{endTime}


 group by user_id

select date(create_time) as betDate,user_id as userId, count(*) as totalCount,sum(bet_amount) as totalAmount,sum(valid_bet_amount) as totalValidBetAmount
,sum(win_lose_amount) as winLoseAmount,sum(win_amount) as totalWinAmount from t_bet_record


        and platform_id = #{platformId}


        and lottery_id = #{lotteryId}


        and user_id = #{userId}


        and issue_no = #{issueNo}


        and status = #{status}


        and play_type_code = #{playTypeCode}


        and play_type_detail_code = #{playTypeDetailCode}


        and create_time >=#{beginTime}


        and create_time <#{endTime}


group by date(create_time) order by betDate desc

select lottery_id as lotteryId, count(*) as totalCount,sum(bet_amount) as totalAmount ,sum(valid_bet_amount) as totalValidBetAmount
,sum(win_lose_amount) as winLoseAmount,sum(win_amount) as totalWinAmount
from t_bet_record


         and platform_id = #{platformId}


         and lottery_id = #{lotteryId}


         and user_id = #{userId}


         and issue_no = #{issueNo}


         and status = #{status}


         and play_type_code = #{playTypeCode}


         and play_type_detail_code = #{playTypeDetailCode}


         and create_time >=#{beginTime}


         and create_time <#{endTime}


 group by lottery_id

select play_type_code as playTypeCode, count(*) as totalCount,sum(bet_amount) as totalAmount,sum(valid_bet_amount) as totalValidBetAmount
,sum(win_lose_amount) as winLoseAmount,sum(win_amount) as totalWinAmount from t_bet_record


        and platform_id = #{platformId}


        and lottery_id = #{lotteryId}


        and user_id = #{userId}


        and issue_no = #{issueNo}


        and status = #{status}


        and play_type_code = #{playTypeCode}


        and play_type_detail_code = #{playTypeDetailCode}


        and create_time >=#{beginTime}


        and create_time <#{endTime}


group by play_type_code

select play_type_detail_code as playTypeDetailCode, count(*) as totalCount,sum(bet_amount) as totalAmount,sum(valid_bet_amount) as totalValidBetAmount
,sum(win_lose_amount) as winLoseAmount,sum(win_amount) as totalWinAmount from t_bet_record


        and platform_id = #{platformId}


        and lottery_id = #{lotteryId}


        and user_id = #{userId}


        and issue_no = #{issueNo}


        and status = #{status}


        and play_type_code = #{playTypeCode}


        and play_type_detail_code = #{playTypeDetailCode}


        and create_time >=#{beginTime}


        and create_time <#{endTime}


group by play_type_detail_code
