mapper.select:
select a.*, b.name as lotteryName from t_bet_record a left t_lottery b on a.lottery_id = b.id

     and a.id = #{id}
     and a.user_id = #{userId}
     and a.bet_amount = #{betAmount}
     and a.can_win_amount = #{canWinAmount}
     and a.bet_content = #{betContent}
     and a.display_bet_content = #{displayBetContent}
     and a.bet_odds = #{betOdds}
     and a.bet_additional_odds = #{betAdditionalOdds}
     and a.platform_id = #{platformId}
     and a.platform_user_id = #{platformUserId}
     and a.lottery_id = #{lotteryId}
     and a.issue_no = #{issueNo}
     and a.play_type_name like concat('%', #{playTypeName}, '%')
     and a.display_play_type_name like concat('%', #{displayPlayTypeName}, '%')
     and a.win = #{win}
     and a.win_lose_amount = #{winLoseAmount}
     and a.play_type_code = #{playTypeCode}
     and a.play_type_detail_code = #{playTypeDetailCode}
     and a.bet_no = #{betNo}
     and a.status = #{status}
     and a.cancel_type = #{cancelType}
     and a.bet_time = #{betTime}
     and a.count_time = #{countTime}
     and a.create_by = #{createBy}
     and a.update_by = #{updateBy}



                and bet_time >= #{beginTime}


                and count_time >= #{beginTime}


                and count_time >= #{beginTime}






                and bet_time <= #{endTime}


                and count_time <= #{endTime}


                and count_time <= #{endTime}




    order by a.id desc

mapper.update:
update t_bet_record
user_id = #{userId}, bet_amount = #{betAmount}, can_win_amount = #{canWinAmount}, bet_content = #{betContent}, display_bet_content = #{displayBetContent}, bet_odds = #{betOdds}, bet_additional_odds = #{betAdditionalOdds}, platform_id = #{platformId}, platform_user_id = #{platformUserId}, lottery_id = #{lotteryId}, issue_no = #{issueNo}, play_type_name = #{playTypeName}, display_play_type_name = #{displayPlayTypeName}, win = #{win}, win_lose_amount = #{winLoseAmount}, play_type_code = #{playTypeCode}, play_type_detail_code = #{playTypeDetailCode}, bet_no = #{betNo}, status = #{status}, cancel_type = #{cancelType}, bet_time = #{betTime}, count_time = #{countTime}, create_time = #{createTime}, update_time = #{updateTime}, create_by = #{createBy}, update_by = #{updateBy},
where id = #{id}

mapper.select:
select count(*) as totalCount,coalesce(sum(bet_amount),0) as totalAmount,coalesce(sum(win_lose_amount),0) as winLoseAmount from t_bet_record


        and platform_id = #{platformId}

    and status = 1 and create_time >=#{beginTime} and create_time <#{endTime}

select count(*) as totalCount,coalesce(sum(bet_amount),0) as totalAmount,coalesce(sum(win_lose_amount),0) as winLoseAmount from t_bet_record


        and platform_id = #{platformId}


        and lottery_id = #{lotteryId}

    and status = 1 and create_time >=#{beginTime} and create_time <#{endTime}

select user_id as userId, count(*) as totalCount,coalesce(sum(bet_amount),0) as totalAmount,coalesce(sum(win_lose_amount),0) as winLoseAmount from t_bet_record
where platform_id = #{platformId}

    and lottery_id = #{lotteryId}


    and user_id = #{userId}

and status = 1 and create_time >=#{beginTime} and create_time <#{endTime} group by user_id
