mapper.select:
select a.*,b.platform_id as platformId,c.name as platformName,b.platform_user_id as platformUserId from t_lottery_prize_record a
inner join t_user b on a.user_id = b.id inner join t_platform c on b.platform_id = c.id

     and a.lottery_id = #{lotteryId}
     and a.amount = #{amount}
     and a.user_id = #{userId}
     and a.is_virtual = #{isVirtual}
     and b.platform_id = #{platformId}
     and b.platform_user_id = #{platformUserId}
     and a.create_time >= #{beginTime}
     and a.create_time <= #{endTime}

order by a.create_time desc

mapper.update:
update t_lottery_prize_record
lottery_id = #{lotteryId}, amount = #{amount}, user_id = #{userId}, create_time = #{createTime}, update_time = #{updateTime},
where id = #{id}

mapper.select:
select coalesce(sum(amount),0) as totalPrizeAmount,count(*) as totalPrizeCount,count(distinct user_id) as totalPrizePeopleCount
 ,date(create_time) as statDate from t_lottery_prize_record


        and lottery_id = #{lotteryId}


        and user_id = #{userId}


        and create_time >= #{beginTime}


        and create_time <= #{endTime}

        and is_virtual = 0

        group by statDate order by statDate desc

select coalesce(sum(amount),0) as totalPrizeAmount,count(*) as totalPrizeCount,count(distinct user_id) as totalPrizePeopleCount
,lottery_id as lotteryId from t_lottery_prize_record


        and user_id = #{userId}


        and create_time >= #{beginTime}


        and create_time <= #{endTime}

    and is_virtual = 0

group by lottery_id order by lottery_id desc
